import React from 'react';
import { Chart, ChartCanvas, CandlestickSeries, XAxis, YAxis, CrossHairCursor, MouseCoordinateX,
    MouseCoordinateY, discontinuousTimeScaleProviderBuilder } from 'react-financial-charts';
import { timeFormat } from 'd3-time-format';
import * as d3 from 'd3-format';
import axios from 'axios';
import '../css/main.css';

function Main() {
    const [data, setData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://34.22.73.38:8080/stock-data-for-graph', {
                    params: {
                        stockCode: '005930',
                        startDate: '2024-05-05',
                        endDate: '2024-06-20'
                    }
                });
                const parsedData = response.data.map(item => ({
                    ...item,
                    date: new Date(item.date)
                }));
                setData(parsedData);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    const GoStart = () => {
        window.location.href = '/';
    };

    const xScaleProvider = discontinuousTimeScaleProviderBuilder().inputDateAccessor(d => d.date);
    const { data: chartData, xScale, xAccessor, displayXAccessor } = xScaleProvider(data);

    const xExtents = [xAccessor(chartData[0]), xAccessor(chartData[chartData.length - 1])];

    return (
        <div id="App">
            <div id="header_main">
                <button id="logout_btn" onClick={GoStart}>
                    <span>로그아웃</span>
                </button>
            </div>
            <div id="body_main">
                <div id="stock_info">
                    주가정보
                </div>
                <div id="chart">
                    <div id="blank"/>
                    <ChartCanvas
                        height={500}
                        width={1100}
                        ratio={3}
                        margin={{ left: 50, right: 50, top: 10, bottom: 30 }}
                        data={chartData}
                        seriesName="MSFT"
                        xScale={xScale}
                        xAccessor={xAccessor}
                        displayXAccessor={displayXAccessor}
                        xExtents={xExtents}
                        style={{ backgroundColor: 'white' }}
                    >
                        <Chart id={1} yExtents={d => [d.high, d.low]}>
                            <XAxis axisAt="bottom" orient="bottom" />
                            <YAxis axisAt="left" orient="left" ticks={5} />
                            <MouseCoordinateX
                                at="bottom"
                                orient="bottom"
                                displayFormat={timeFormat("%Y-%m-%d")}
                            />
                            <MouseCoordinateY
                                at="left"
                                orient="left"
                                displayFormat={d3.format(".2f")}
                            />
                            <CandlestickSeries />
                            <CrossHairCursor />
                        </Chart>
                    </ChartCanvas>
                </div>
                <div id="contents">
                    보유금액, 충전, 매수, 매매 등등
                </div>
            </div>
        </div>
    );
}

export default Main;
