        e.preventDefault();
        console.log("Form data:", inputs);
        try {
            const response = await axios.post('/api/student/signup', inputs);
            console.log('Form Submit success:', response.data);
            alert("회원가입 성공");
            window.location.href='/';
        } catch (error) {
            console.error('Form Submit error:', error);
            if (error.response) {
                console.log("Error response data:", error.response.data);
                alert(`${error.response.data}`);
            } else {
                alert('An error occurred. Please try again later.');
            }
        }
    };
  return (
    <div id="App">
        <div id="header"/>
        <div id="body">
            <form id="MembershipForm" onSubmit={handleSubmit}>
                <h2>회원가입</h2>
                <input type="text" name="userID" placeholder="아이디"/><hr/>
                <input type="text" name="userPW" placeholder="비밀번호"/><hr/>
                <input type="text" name="userEmail" placeholder="이메일"/><hr/>
                <button type="submit" id="membership_btn">
                    <span>회원가입</span>
                </button>
            </form>
        </div>
        <div id="footer"/>
    </div>
  );
}

export default Membership;
